# The articles are in <a> tags with class "PostCard_post-card__z_Sqq" inside the section with class "LandingPageSection_root__nbLb0"
# The title is in <h3> with class "PostCard_post-heading__Ob1pu"
# The link is the href of the <a> tag
# The date is in a div with class "PostList_post-date__djrOA"

post_cards = soup.select("a.PostCard_post-card__z_Sqq")

for card in post_cards:
    title_tag = card.select_one("h3.PostCard_post-heading__Ob1pu")
    date_tag = card.select_one("div.PostList_post-date__djrOA")
    link = card.get("href", "").strip()
    title = title_tag.get_text(strip=True) if title_tag else None
    date = date_tag.get_text(strip=True) if date_tag else None

    articles.append({
        "title": title,
        "link": link,
        "date": date
    })

print(articles)